package com.android.activityfragmentexample.presentation.fragmentone


import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.navigation.fragment.findNavController
import com.android.activityfragmentexample.databinding.FragmentOneBinding
import com.android.activityfragmentexample.model.Person


class FragmentOne : Fragment() {

    private lateinit var binding: FragmentOneBinding


    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?,
    ): View? {
        binding = FragmentOneBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        setClickListener()
    }

    private fun setClickListener() {
        binding.btnNavigate.setOnClickListener{
            navigateToFragmentTwo()
        }

        binding.btnSendDataNavigate.setOnClickListener{
            navigateToFragmentTwo(getPerson())
        }
    }



    private fun getPerson(): Person? {
        return Person(
            name = "Tony Stark",
            job  = "super man",
            profileDesc = "help People",
            profilePicUrl = "data:image/jpeg;base64,"

        )

    }

    private fun navigateToFragmentTwo(person: Person? = null) {

        val action = FragmentOneDirections.actionFragmentOneToFragmentTwo(person)
        findNavController().navigate(action)
    }



}